package flutter.curiosity;import com.google.zxing.Result;import com.google.zxing.ResultPoint;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;public class Utils {    public static Map<String, Object> toMap(Result result) {        if (result == null) return null;        Map<String, Object> data = new HashMap<>();        data.put("message", result.getText());        data.put("type", result.getBarcodeFormat().ordinal());        if (result.getResultPoints() != null) {            List<Map<String, Object>> resultPoints = new ArrayList<>();            for (ResultPoint point : result.getResultPoints()) {                Map<String, Object> pointMap = new HashMap<>();                pointMap.put("x", point.getX());                pointMap.put("y", point.getY());                resultPoints.add(pointMap);            }            data.put("points", resultPoints);        }        return data;    }}