import 'package:flutter/material.dart';import 'package:flutter_curiosity/flutter_curiosity.dart';import 'package:flutter_waya/flutter_waya.dart';class ScanPage extends StatefulWidget {  @override  ScanCodePageState createState() => ScanCodePageState();}class ScanCodePageState extends State<ScanPage> {  ScannerController controller = ScannerController();  bool isFirst = true;  @override  void initState() {    super.initState();    initController();  }  Future<void> initController() async {    controller.initialize();    controller.addListener(() {      String code = controller.code;      String type = controller.type;      if (code != null) {        showToast(code);        print(code + '===' + type);        if (isFirst) {          isFirst = false;        }      }    });  }  bool flashMode = false;  @override  Widget build(BuildContext context) {    return Scaffold(      body: ScannerBox(        borderColor: Colors.blue,        scannerColor: Colors.blue,        scannerStrokeWidth: 4,        hornStrokeWidth: 3,        size: Size(ScreenFit.getWidth(0) * 0.6, ScreenFit.getWidth(0) * 0.6),        child: Scanner(          controller: controller,        ),      ),    );  }}