import 'package:flutter/material.dart';import 'package:flutter_curiosity/curiosity.dart';class ScanPage extends StatefulWidget {  @override  ScanCodePageState createState() => ScanCodePageState();}class ScanCodePageState extends State<ScanPage> {  ScannerController controller = ScannerController();  @override  void initState() {    super.initState();    initController();  }  @override  void dispose() {//    AlertTools.close();    super.dispose();  }  bool isFirst = true;  Future<void> initController() async {    controller.addListener(() {      String code = controller.code;      String type = controller.type;      if (code != null) {//        showToast(code);        print(code + '===' + type);        if (isFirst) {          isFirst = false;        }      }    });  }  bool flashMode = false;  @override  Widget build(BuildContext context) {    return Scaffold(        body: Stack(          children: <Widget>[            ScannerBox(              child: Container(                  child: Scanner(                    controller: controller,                  )),            ),          ],        )    );  }}