import 'package:flutter/material.dart';import 'package:flutter_curiosity/curiosity.dart';class ScanPage extends StatefulWidget {  @override  ScanCodePageState createState() => ScanCodePageState();}class ScanCodePageState extends State<ScanPage> {  ScanController controller = ScanController();  @override  void initState() {    super.initState();    initController();  }  bool isFirst = true;  Future<void> initController() async {    controller.addListener(() {      final result = controller.result;      if (result != null) {        if (isFirst) {          isFirst = false;        }      }    });  }  bool flashMode = false;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Stack(        children: <Widget>[          ScanImageView(            child: Container(                color: Colors.amberAccent,                child: ScanView(                  controller: controller,                )),          )        ],      ),    );  }}