import 'dart:typed_data';import 'package:flutter_curiosity/constant/Constant.dart';import 'package:flutter_curiosity/scan/CameraScansResult.dart';/// qr scanclass CameraScans {  /// scan qr image in path  ///  /// [path] your qr image path  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<CameraScansResult> scanImagePath(String path) async =>      CameraScansResult.formMap(await channel.invokeMethod('scanImagePath', {        "path": path,      }));  /// scan qr image in url  ///  /// [url] your qr image url  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<CameraScansResult> scanImageUrl(String url) async =>      CameraScansResult.formMap(await channel.invokeMethod('scanImageUrl', {        "url": url,      }));  /// scan qr image in memory  ///  /// [uint8list] your qr image memory  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<CameraScansResult> scanImageMemory(Uint8List uint8list) async =>      CameraScansResult.formMap(await channel.invokeMethod('scanImageMemory', {        "uint8list": uint8list,      }));}typedef void CameraScansResultCallback(String result);