/// barcode pointclass ScanPoint {  /// barcode point x  final double x;  /// barcode point y  final double y;  ScanPoint(this.x, this.y);  @override  String toString() {    return 'ScanPoint{x: $x, y: $y}';  }  @override  bool operator ==(Object other) =>      identical(this, other) ||          other is ScanPoint &&              runtimeType == other.runtimeType &&              x == other.x &&              y == other.y;  @override  int get hashCode => x.hashCode ^ y.hashCode;}