import 'dart:typed_data';import 'package:flutter_curiosity/constant/Constant.dart';import 'package:flutter_curiosity/scan/ScanResult.dart';/// qr scanclass ScanUtils {  /// scan qr image in path  ///  /// [path] your qr image path  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<ScanResult> scanImagePath(String path) async =>      ScanResult.formMap(await channel.invokeMethod('scanImagePath', {        "path": path      }));  /// scan qr image in url  ///  /// [url] your qr image url  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<ScanResult> scanImageUrl(String url) async =>      ScanResult.formMap(await channel.invokeMethod('scanImageUrl', {        "url": url      }));  /// scan qr image in memory  ///  /// [uint8list] your qr image memory  ///  /// It will return your scan result.if not found qr,will return empty.  static Future<ScanResult> scanImageMemory(Uint8List uint8list) async =>      ScanResult.formMap(await channel.invokeMethod('scanImageMemory', {        "uint8list": uint8list      }));}