import 'package:flutter/material.dart';import 'package:flutter_curiosity/scan/view/ScanPainter.dart';import 'package:flutter_curiosity/utils/Utils.dart';class ScanImageView extends StatefulWidget {  final Widget child;  ScanImageView({Key key, this.child}) : super(key: key);  @override  ScanImageViewState createState() => ScanImageViewState();}class ScanImageViewState extends State<ScanImageView> with TickerProviderStateMixin {  AnimationController controller;  @override  void initState() {    super.initState();    controller = AnimationController(vsync: this, duration: Duration(milliseconds: 1000));    controller.repeat(reverse: true);  }  @override  void dispose() {    controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    Size size = Size(Utils        .getSize()        .width * 0.8, Utils        .getSize()        .width * 0.8);    return AnimatedBuilder(        animation: controller,        builder: (BuildContext context, Widget child) =>            CustomPaint(              foregroundPainter: ScanPainter(value: controller.value,                  borderColor: Colors.white, scanColor: Colors.black),              child: widget.child,              willChange: true,            ));  }}