import 'package:flutter_curiosity/constant/Constant.dart';import 'package:flutter_curiosity/tools/Tools.dart';class GPSTools {  //判断GPS是否开启，GPS或者AGPS开启一个就认为是开启的  static Future<bool> getStatus() async {    Tools.supportPlatform();    return await methodChannel.invokeMethod('getStatus');  }  // 跳转到设置页面让用户自己手动开启  static Future jumpSetting() async {    Tools.supportPlatform();    await methodChannel.invokeMethod('jumpSetting');  }}