import 'dart:io';import 'package:flutter_curiosity/constant/Constant.dart';class NativeUtils {  static Future clearAllCookie() async {    await channel.invokeMethod('clearAllCookie');  }  static Future installApp(String apkPath) async {    if (Platform.isAndroid) {      await channel.invokeMethod('installApp', {'apkPath': apkPath});    }  }  static Future getAllCookie(String url) async {    final result = await channel.invokeMethod('getAllCookie', {'url': url});    return result;  }  static Future getFilePathSize(String filePath) async {    final result = await channel.invokeMethod('getFilePathSize', {'filePath': filePath});    return result;  }  static Future deleteFolder(String folderPath) async {    await channel.invokeMethod('deleteFolder', {'folderPath': folderPath});  }  static Future filePath(String filePath) async {    await channel.invokeMethod('deleteFile', {'filePath': filePath});  }  static Future goToMarket(String packageName, [String marketPackageName]) async {    if (Platform.isIOS) {      await channel.invokeMethod('goToMarket', {'packageName': packageName});    } else if (Platform.isAndroid) {      await channel.invokeMethod('goToMarket', {'packageName': packageName, 'marketPackageName': marketPackageName});    }  }  static Future isInstallApp(String packageName) async {    if (Platform.isAndroid) {      final result = await channel.invokeMethod('isInstallApp', {'packageName': packageName});      return result;    }  }  static Future exitApp() async {    await channel.invokeMethod('exitApp');  }}