import 'dart:io';import 'package:flutter_curiosity/constant/Constant.dart';import 'package:flutter_curiosity/utils/Utils.dart';class NativeUtils {  //清楚cookie  static Future clearAllCookie() async {    Utils.supportPlatform();    await channel.invokeMethod('clearAllCookie');  }  //获取cookie  static Future getAllCookie(String url) async {    Utils.supportPlatform();    final result = await channel.invokeMethod('getAllCookie', {'url': url});    return result;  }  //安装apk  仅支持android  static Future installApp(String apkPath) async {    Utils.supportPlatform();    if (Platform.isAndroid) {      await channel.invokeMethod('installApp', {'apkPath': apkPath});    }  }  //获取文件夹或文件大小  static Future getFilePathSize(String path) async {    Utils.supportPlatform();    final result = await channel.invokeMethod('getFilePathSize', {'filePath': path});    return result;  }  //删除目录  static Future deleteDirectory(String directoryPath) async {    Utils.supportPlatform();    await channel.invokeMethod('deleteDirectory', {'directoryPath': directoryPath});  }  //删除文件  static Future deleteFile(String filePath) async {    Utils.supportPlatform();    await channel.invokeMethod('deleteFile', {'filePath': filePath});  }  //解压文件  static Future unZipFile(String filePath) async {    Utils.supportPlatform();    await channel.invokeMethod('unZipFile', {'filePath': filePath});  }  //去应用市场 android 安装多个应用商店时会弹窗选择，ios app store  static Future goToMarket(String packageName, [String marketPackageName]) async {    Utils.supportPlatform();    if (Platform.isIOS) {      await channel.invokeMethod('goToMarket', {'packageName': packageName});    } else if (Platform.isAndroid) {      await channel.invokeMethod('goToMarket', {'packageName': packageName, 'marketPackageName': marketPackageName});    }  }  //是否安装某个app  仅支持android  static Future isInstallApp(String packageName) async {    Utils.supportPlatform();    if (Platform.isAndroid) {      final result = await channel.invokeMethod('isInstallApp', {'packageName': packageName});      return result;    }  }  //退出app  static Future exitApp() async {    Utils.supportPlatform();    await channel.invokeMethod('exitApp');  }}